import axios from '@/utils/axiosConfig';
import AutoComplete from 'primevue/autocomplete';
export default {
  name: 'LeaveForm',
  components: {
    AutoComplete
  },
  async created() {
    await this.loadLeaveTypes();
    await this.loadUsers();
    await this.loadQuotaData();
    // Initialize with limited users
    this.filteredUsers = this.users.slice(0, this.maxDisplayUsers);
  },
  data() {
    return {
      formData: {
        leaveType: '',
        startDateTime: null,
        endDateTime: null,
        reason: '',
        delegationOption: 'no',
        delegateName: '',
        delegatePosition: '',
        delegateDepartment: '',
        delegateContact: '',
        workDetails: '',
        attachments: []
      },
      leaveTypes: [],
      delegationOptions: [{
        label: 'ไม่มีการมอบหมายงาน',
        value: 'no'
      }, {
        label: 'มีการมอบหมายงาน',
        value: 'yes'
      }],
      users: [],
      filteredUsers: [],
      selectedDelegate: null,
      maxDisplayUsers: 50,
      quotaData: {},
      selectedQuota: null
    };
  },
  computed: {
    currentUserName() {
      const firstName = localStorage.getItem('soc_firstname') || '';
      const lastName = localStorage.getItem('soc_lastname') || '';
      return `${firstName} ${lastName}`.trim();
    },
    currentUserRole() {
      return localStorage.getItem('soc_role') || '';
    },
    currentUserPosition() {
      return localStorage.getItem('soc_position') || 'ไม่ระบุตำแหน่ง';
    },
    calculateDays() {
      if (this.formData.startDateTime && this.formData.endDateTime) {
        const start = new Date(this.formData.startDateTime);
        const end = new Date(this.formData.endDateTime);

        // คำนวณชั่วโมงทำงานจริง (9-12, 13-18 = 8 ชม./วัน)
        const hoursPerDay = 8;
        let totalHours = 0;
        const startDate = new Date(start);
        startDate.setHours(0, 0, 0, 0);
        const endDate = new Date(end);
        endDate.setHours(0, 0, 0, 0);
        if (startDate.getTime() === endDate.getTime()) {
          // วันเดียวกัน - คำนวณชั่วโมงตรงๆ
          totalHours = this.calculateWorkHours(start, end);
        } else {
          // หลายวัน
          const current = new Date(startDate);
          while (current <= endDate) {
            const day = current.getDay();
            if (day !== 0 && day !== 6) {
              // ไม่นับ เสาร์-อาทิตย์
              if (current.getTime() === startDate.getTime()) {
                // วันแรก - นับจากเวลาเริ่มถึงสิ้นสุดวัน
                const dayEnd = new Date(current);
                dayEnd.setHours(18, 0, 0, 0);
                totalHours += this.calculateWorkHours(start, dayEnd);
              } else if (current.getTime() === endDate.getTime()) {
                // วันสุดท้าย - นับจากเริ่มวันถึงเวลาสิ้นสุด
                const dayStart = new Date(current);
                dayStart.setHours(9, 0, 0, 0);
                totalHours += this.calculateWorkHours(dayStart, end);
              } else {
                // วันกลาง - นับเต็มวัน
                totalHours += hoursPerDay;
              }
            }
            current.setDate(current.getDate() + 1);
          }
        }
        const days = (totalHours / hoursPerDay).toFixed(1);
        return `${days} วัน (${totalHours.toFixed(1)} ชม.)`;
      }
      return '0 วัน (0 ชม.)';
    },
    leaveTypesWithQuota() {
      return this.leaveTypes.map(type => {
        const quota = this.quotaData[type.value];
        const disabled = quota && quota.remainingDays <= 0;
        return {
          ...type,
          disabled: disabled
        };
      });
    }
  },
  methods: {
    // คำนวณชั่วโมงทำงานระหว่างสองเวลา (9-12, 13-18)
    calculateWorkHours(start, end) {
      const startHour = start.getHours() + start.getMinutes() / 60;
      const endHour = end.getHours() + end.getMinutes() / 60;
      let hours = 0;
      // ช่วงเช้า 9-12
      const morningStart = Math.max(startHour, 9);
      const morningEnd = Math.min(endHour, 12);
      if (morningEnd > morningStart) {
        hours += morningEnd - morningStart;
      }
      // ช่วงบ่าย 13-18
      const afternoonStart = Math.max(startHour, 13);
      const afternoonEnd = Math.min(endHour, 18);
      if (afternoonEnd > afternoonStart) {
        hours += afternoonEnd - afternoonStart;
      }
      return Math.max(0, hours);
    },
    // ตรวจสอบและปรับเวลาให้อยู่ในช่วงที่กำหนด
    validateTime(field) {
      const dateField = field === 'start' ? 'startDateTime' : 'endDateTime';
      if (!this.formData[dateField]) return;
      const date = new Date(this.formData[dateField]);
      const hour = date.getHours();
      const minute = date.getMinutes();
      let adjusted = false;
      // ก่อน 9 โมง → ปรับเป็น 9:00
      if (hour < 9) {
        date.setHours(9, 0, 0, 0);
        adjusted = true;
      }
      // 12:01-12:59 → ปรับเป็น 13:00
      else if (hour === 12 && minute > 0) {
        date.setHours(13, 0, 0, 0);
        adjusted = true;
      }
      // หลัง 18 โมง → ปรับเป็น 18:00
      else if (hour > 18 || hour === 18 && minute > 0) {
        date.setHours(18, 0, 0, 0);
        adjusted = true;
      }
      if (adjusted) {
        this.formData[dateField] = date;
        this.$toast.add({
          severity: 'info',
          summary: 'ปรับเวลา',
          detail: 'เวลาถูกปรับให้อยู่ในช่วงเวลาทำการ (09:00-12:00, 13:00-18:00)',
          life: 3000
        });
      }
    },
    async loadLeaveTypes() {
      try {
        const response = await axios.get('/api/leave/leave-types');
        this.leaveTypes = response.data;
      } catch (error) {
        // Fallback to default types if API fails
        this.leaveTypes = [{
          label: 'ลาป่วย',
          value: 'sick'
        }, {
          label: 'ลากิจ',
          value: 'personal'
        }, {
          label: 'ลาพักร้อน',
          value: 'vacation'
        }];
      }
    },
    optionDisabled(option) {
      return option.disabled;
    },
    async loadQuotaData() {
      try {
        const userId = localStorage.getItem('soc_user_id');
        const response = await axios.get(`/api/leave/quota/${userId}`);
        this.quotaData = response.data;

        // Update selected quota if leave type is already selected
        if (this.formData.leaveType) {
          this.selectedQuota = this.quotaData[this.formData.leaveType];
        }
      } catch (error) {
        this.$toast.add({
          severity: 'error',
          summary: 'ข้อผิดพลาด',
          detail: 'ไม่สามารถโหลดข้อมูลโควต้าได้',
          life: 3000
        });
      }
    },
    onLeaveTypeChange() {
      if (this.formData.leaveType && this.quotaData[this.formData.leaveType]) {
        this.selectedQuota = this.quotaData[this.formData.leaveType];
      } else {
        this.selectedQuota = null;
      }
    },
    getQuotaClass(remainingDays) {
      if (remainingDays <= 0) return 'quota-zero';
      if (remainingDays <= 2) return 'quota-low';
      return 'quota-normal';
    },
    async submitForm() {
      if (!this.formData.leaveType) {
        this.$toast.add({
          severity: 'error',
          summary: 'ข้อผิดพลาด',
          detail: 'กรุณาเลือกประเภทการลา',
          life: 3000
        });
        return;
      }

      // Check quota before submitting
      if (this.selectedQuota && this.selectedQuota.remainingDays <= 0) {
        this.$toast.add({
          severity: 'error',
          summary: 'ข้อผิดพลาด',
          detail: 'โควต้าการลาประเภทนี้หมดแล้ว',
          life: 3000
        });
        return;
      }

      // Check if requested days exceed remaining quota
      const requestedDays = this.calculateTotalDays();
      if (this.selectedQuota && requestedDays > this.selectedQuota.remainingDays) {
        this.$toast.add({
          severity: 'error',
          summary: 'ข้อผิดพลาด',
          detail: `จำนวนวันที่ขอลา (${requestedDays} วัน) เกินโควต้าคงเหลือ (${this.selectedQuota.remainingDays} วัน)`,
          life: 3000
        });
        return;
      }
      if (this.formData.startDateTime && this.formData.endDateTime) {
        if (new Date(this.formData.endDateTime) < new Date(this.formData.startDateTime)) {
          this.$toast.add({
            severity: 'error',
            summary: 'ข้อผิดพลาด',
            detail: 'วันสิ้นสุดการลาต้องไม่เป็นวันก่อนวันเริ่มลา',
            life: 3000
          });
          return;
        }
      }
      try {
        // Upload files first if any
        let uploadedFiles = [];
        if (this.formData.attachments && this.formData.attachments.length > 0) {
          const formData = new FormData();
          this.formData.attachments.forEach(file => {
            formData.append('files', file);
          });
          const uploadResponse = await axios.post('/api/files/upload', formData, {
            headers: {
              'Content-Type': 'multipart/form-data'
            }
          });
          uploadedFiles = uploadResponse.data.files;
        }

        // Calculate total days
        const totalDays = this.calculateTotalDays();

        // Prepare data for API
        const leaveData = {
          user_id: localStorage.getItem('soc_user_id'),
          leave_type: this.formData.leaveType,
          start_datetime: this.formatDateTime(this.formData.startDateTime),
          end_datetime: this.formatDateTime(this.formData.endDateTime),
          total_days: totalDays,
          reason: this.formData.reason || 'ไม่ระบุ',
          has_delegation: this.formData.delegationOption === 'yes',
          delegate_name: this.formData.delegateName || null,
          delegate_position: this.formData.delegatePosition || null,
          delegate_department: this.formData.delegateDepartment || null,
          delegate_contact: this.formData.delegateContact || null,
          work_details: this.formData.workDetails || null,
          attachments: uploadedFiles
        };

        // Send to backend
        const response = await axios.post('/api/leave', leaveData, {
          headers: {}
        });
        this.$toast.add({
          severity: 'success',
          summary: 'สำเร็จ',
          detail: 'บันทึกคำขอลางานเรียบร้อยแล้ว',
          life: 3000
        });
        this.$emit('submit-leave', response.data);
        this.resetForm();
      } catch (error) {
        let errorMessage = 'ไม่สามารถบันทึกข้อมูลได้ กรุณาลองใหม่อีกครั้ง';
        if (error.response?.data?.error) {
          errorMessage = error.response.data.error;
        } else if (error.message) {
          errorMessage = error.message;
        }
        this.$toast.add({
          severity: 'error',
          summary: 'เกิดข้อผิดพลาด',
          detail: errorMessage,
          life: 5000
        });
      }
    },
    calculateTotalDays() {
      if (this.formData.startDateTime && this.formData.endDateTime) {
        const start = new Date(this.formData.startDateTime);
        const end = new Date(this.formData.endDateTime);
        start.setHours(0, 0, 0, 0);
        end.setHours(0, 0, 0, 0);
        let count = 0;
        const current = new Date(start);
        while (current <= end) {
          const day = current.getDay();
          if (day !== 0 && day !== 6) count++;
          current.setDate(current.getDate() + 1);
        }
        return count;
      }
      return 0;
    },
    formatDateTime(date) {
      if (!date) return null;
      return new Date(date).toISOString();
    },
    resetForm() {
      this.formData = {
        leaveType: '',
        startDateTime: null,
        endDateTime: null,
        reason: '',
        delegationOption: 'no',
        delegateName: '',
        delegatePosition: '',
        delegateDepartment: '',
        delegateContact: '',
        workDetails: '',
        attachments: []
      };
      this.selectedDelegate = null;
    },
    handleFileUpload(event) {
      const files = Array.from(event.target.files);
      this.formData.attachments = [...this.formData.attachments, ...files];
    },
    removeFile(index) {
      this.formData.attachments.splice(index, 1);
    },
    async loadUsers() {
      try {
        const response = await axios.get('/api/users');
        if (response.data && Array.isArray(response.data)) {
          // กรองผู้ใช้ปัจจุบันออก
          const currentUserName = this.currentUserName;
          this.users = response.data.filter(user => {
            const userName = `${user.firstname} ${user.lastname}`.trim();
            return userName !== currentUserName;
          }).map(user => ({
            ...user,
            displayName: `${user.firstname} ${user.lastname}`.trim()
          }));
          this.filteredUsers = this.users.slice(0, this.maxDisplayUsers);
        } else {
          this.setFallbackUsers();
        }
      } catch (error) {
        this.setFallbackUsers();
      }
    },
    setFallbackUsers() {
      this.users = [{
        id: 1,
        username: 'admin',
        firstname: 'Admin',
        lastname: 'User',
        role: 'admin',
        displayName: 'Admin User'
      }, {
        id: 2,
        username: 'hr',
        firstname: 'HR',
        lastname: 'Manager',
        role: 'hr',
        displayName: 'HR Manager'
      }, {
        id: 3,
        username: 'john',
        firstname: 'John',
        lastname: 'Doe',
        role: 'user',
        displayName: 'John Doe'
      }];
      this.filteredUsers = this.users;
    },
    searchUsers(event) {
      const query = event.query.toLowerCase().trim();
      if (query === '') {
        // แสดงแค่ 50 คนแรกเมื่อไม่มีการค้นหา
        this.filteredUsers = this.users.slice(0, this.maxDisplayUsers);
      } else {
        // ค้นหาและจำกัดผลลัพธ์
        const filtered = this.users.filter(user => user.displayName.toLowerCase().includes(query) || user.username.toLowerCase().includes(query) || user.position && user.position.toLowerCase().includes(query) || user.department && user.department.toLowerCase().includes(query));
        this.filteredUsers = filtered.slice(0, this.maxDisplayUsers);
      }
    },
    showAllUsers() {
      // แสดงแค่ 50 คนแรกเมื่อคลิก dropdown
      this.filteredUsers = this.users.slice(0, this.maxDisplayUsers);
    },
    onDelegateSelect(event) {
      const user = event.value;
      if (user) {
        this.formData.delegateName = user.displayName;
        this.formData.delegatePosition = user.position || '';
        this.formData.delegateDepartment = user.department || '';
      }
    }
  }
};
